mapper.select:
  		SELECT count(id_mem)
		FROM following
		WHERE id_co = #{id}

mapper.insert:
SELECT 'OFFER'||nvl((SUBSTR(MAX(offer_cd),6,5))+1,10001) FROM offer
DECLARE v_offer_cd VARCHAR2(100) := #{offerCd}; v_tech VARCHAR2(100) := #{tech}; BEGIN INSERT INTO offer (offer_cd, co_nm, wksite, tech, ma_buss, bnft, saly, buss_start_dt, cls_dt, project_nm, ttL) VALUES (#{offerCd}, #{coNm}, #{wksite}, #{tech}, #{maBuss}, #{bnft}, #{saly}, #{bussStartDt}, #{clsDt}, #{projectNm}, #{ttl});
INSERT INTO job_search_st (offer_cd, id, job_search_no, accept_status ) VALUES(#{offerCd}, #{id}, #{jobSearchNo}, 'e1' );
UPDATE job_search_notiwr SET prop = prop + 1 WHERE job_search_no = #{jobSearchNo};
FOR i IN (SELECT regexp_substr(v_tech, '[^,]+', 1, level) AS tech FROM dual CONNECT BY regexp_substr(v_tech, '[^,]+', 1, level) IS NOT NULL) LOOP INSERT INTO OFFER_SKILL (OFFER_CD, TECH) VALUES (v_offer_cd, i.tech); END LOOP; END;
