mapper.select:
SELECT R.TTL, K.SKILL, C.CO_NM, get_common(WKSITE) AS WKSITE, R.EXPR_DT, R.CO_IMG, R.RECRUIT_NO, (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id}) AS scrapYn, (SELECT F.ID_CO FROM FOLLOWING F WHERE R.ID = F.ID_CO AND F.ID_MEM = #{id}) AS followYn, get_common(MA_BUSS)AS MA_BUSS, R.QUAL_COND, R.PREF_COND, R.WELF, get_common(CARR)AS CARR, get_common(SHCR)AS SHCR, R.CO_INTRO, R.RECRUIT_NO, C.ID AS ID_CO FROM RECRUIT_ANUN R JOIN CO C ON R.ID = C.ID JOIN (SELECT recruit_no , LISTAGG(get_common(SKILL), ',') WITHIN GROUP (ORDER BY SKILL_NO) as SKILL FROM RECRUIT_SKILL GROUP BY recruit_no) K ON R.RECRUIT_NO = K.RECRUIT_NO WHERE R.RECRUIT_NO = #{recruitNo}
AND EXISTS (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id})
AND EXISTS (SELECT F.ID_CO FROM FOLLOWING F WHERE R.ID = F.ID_CO AND F.ID_MEM = #{id})
