mapper.select:
SELECT R.TTL, K.SKILL, C.CO_NM, R.WKSITE, R.EXPR_DT, R.CO_IMG, S.RECRUIT_NO,
(SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id}) AS scrapYn
FROM RECRUIT_ANUN R JOIN CO C ON R.ID = C.ID JOIN RECRUIT_SCRAP S ON R.RECRUIT_NO = S.RECRUIT_NO JOIN MEMBER M ON S.ID = M.ID JOIN RECRUIT_SKILL K ON R.RECRUIT_NO = K.RECRUIT_NO WHERE S.SCRAP_NO IS NOT NULL AND M.ID = #{id}
