mapper.select:
SELECT ID ,NAME ,PW ,PH ,PH_CL ,EMAIL ,BLOG ,POST ,AUTHR ,CLSF ,LOG_CNT ,LTST_TM ,GEN ,BIRTH ,ADDR ,JOIN_DT ,AGE ,API ,NICKNAME ,get_common(clsf) as "clsfname" FROM member
id = #{id}
name = #{name}
join_dt >= #{startDate}
join_dt <= #{lastDate}
email LIKE '%'||'@'||#{server}||'%'
email LIKE '%'||#{userName}||'%'
clsf IN
#{clsf}
ORDER BY join_dt DESC

mapper.update:
UPDATE 	rprt_list
SET 	rprt_st = #{rprtSt}
WHERE   rprt_no = #{rprtNo}

UPDATE 	recruit_anun
SET 	ACCP_DT = to_char(sysdate,'yyyy-MM-dd')
WHERE   RECRUIT_NO = #{recruitNo}

UPDATE 	job_search_notiwr
SET 	ACCP_DT = to_char(sysdate,'yyyy-MM-dd')
WHERE   JOB_SEARCH_NO = #{jobSearchNo}

UPDATE 	intrv_scrn
SET 	ACCP_DT = to_char(sysdate,'yyyy-MM-dd')
WHERE   INTRV_NO = #{intrvNo}
